body,.footer{
    background-color:#0f121b;
    height:100%;
}
.triangle-down,
.second-section+.container,
.col-md-4.col-sm-12,
.site-copyrights,
#disqus_thread,
#inputEpisode,
.menu-item-21993,
.anime-page-link,
.search-in-episodes-list,
.panel-heading{
    display:none!important;
}
.panel-body,
.content.episode-download-container,
.second-section,
.main-section,
#mCSB_1,
#inputEpisode,
.space,
.content,
.header-navbar,
.row{
    background-color:#0f121b;
}
.episode-watch-links  .row,.user-post-info-content .row ,.container.header-navbar-container .row,.anime-list-content{
    background-color:#131722;
}

.container.episode-watch-container .row:first-of-type {

    padding-top: 15px;
    padding-bottom: 15px;
}

.second-section{
    background-image:none;
}
.server-link{
    color:white;
    background-color:#1e1e1e;
    margin-top:10px;
    margin-right:20px;
    border-radius:100px;
}
#iframe-container{
    background-image:none;
}

.header-navbar,
.page-content-container{
    background-color:#131722;
}

li[id^="menu-item-"] a[href] {
    color: white;
}

li[id^="menu-item-"]:hover{
    color:black;
}

#theme-mode,
button[class="btn dropdown-toggle"]
{
    display: none;
}
#ShowSearch i{
  display: inline;
  font-size: 1em;
  line-height: 1;
  color:#101010;
}
#ShowSearch{
  width:43px;
  height:43px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 6px;
  font-size: 16px;
  border-radius:10px;
  background-color:#027e96;
}

#ShowSearch:hover i{
  color: white;
}
div[class="container header-navbar-container"] div[class="row"]{    
    height:10px;
}
div[class="navbar-buttons"]{
    margin:0px !important; 
    padding:0px !important;
}

#searchContent{
    margin-top:100px;
}
input{
  direction: ltr;
}
img[id="lucodeia-no-contents-img"]{
    display:none;
}
div[class="container header-navbar-container"]{
    margin:0;
    padding:0;
}

div[class="col-lg-3 col-md-3 col-sm-4 col-xs-12 col-no-padding col-mobile-no-padding"]{
    background-color:#131722;
}
.anime-card-container,
.anime-card-details,
.episodes-card-container{
    border:none;
    border-radius:10px;
}

a[class="see-all"]{
    border-radius:10px;
}
img{
    border-radius:10px 10px 0px 0px;
}

body,.footer,html{
    background-color:#0f121b;
    max-height:100%;
    overscroll-behavior: contain; /* to control the behavior */

}
.footer{
    height:100%;
}

